import {get, post} from "@/utils/http"

//login
export function login(username, passwd) {
    post("/user/login", {
        createTime: "",
        id: 0,
        password: passwd,
        permissions: 0,
        updateTime: "",
        username: username
    }).then(res => {
        console.log(res);
    }).catch(err => {
        console.log(err);
    })
}

//get all events by list
export function getevents() {
    get("/todo/list/important"
    ).then(res => {
        console.log(res);
    }).catch(err => {
        console.log(err);
    })
}

//get
