import {get, post} from "@/utils/http"

//get all events by list
export function getevents() {
    get("/events/list"
    ).then(res => {
        console.log(res);
    }).catch(err => {
        console.log(err);
    })
}

//get event by id
export function geteventById(ID) {
    get("/events/"+ID
    ).then(res => {
        console.log(res);
    }).catch(err => {
        console.log(err);
    })
}

//
